/* script.js */

/*
Ï†ÑÏ≤¥ Î∂àÎπõ(ÏûëÏùÄ Î∂àÎπõ + ÌÅ∞ Î∂àÎπõ) Í∞ØÏàòÎ•º count Î≥ÄÏàòÏóê Îã¥Ïùå
ÌÅ∞ Î∂àÎπõ Í∞ØÏàòÎ•º blurCount Î≥ÄÏàòÏóê Îã¥Ïùå
htmlÏóêÏÑú stage class ÏÑ†ÌÉùÌï¥ÏÑú stage Î≥ÄÏàòÏóê Îã¥Ïùå
*/
const count     = 350;  
const blurCount = 150;
const stage = document.querySelector(".stage");

/*
- forÎ¨∏(Î∞òÎ≥µÎ¨∏)ÏúºÎ°ú count Ïà´ÏûêÎ≥¥Îã§ ÏûëÏùÑ Ïãú makeLight() Ìï®Ïàò Ïã§Ìñâ
- Ï≤´ ÏãúÏûë ÌõÑ 10 * i (count Î≥¥Îã§ ÏûëÏùÄ Ïàò)ÎßåÌÅº Î∞ÄÎ¶¨Ï¥à(ms)Í∞Ä ÏßÄÎÇòÏïº Ïã§ÌñâÎê®
- JS ÎÇ¥Ïû•Ìï®ÏàòÏù∏ setTimeoutÏóê ÌôîÏÇ¥ÌëúÌï®ÏàòÎ•º Ï†ÅÏö©ÌïòÏó¨
  Ìï®ÏàòÎ•º ÌååÎùºÎØ∏ÌÑ∞Î°ú Ï†ÑÎã¨ÌïòÏó¨ ÌïúÎ≤à Ïã§Ìñâ ÌõÑ Ï¢ÖÎ£å
*/
for (let i = 0; i < count; i++) {
  setTimeout(() => {
    makeLight(i);
  }, 10 * i);
}

/*
- forÎ¨∏ÏïàÏóêÏÑú makeLight() Ìï®ÏàòÍ∞Ä ÌïúÎ≤à Ï¢ÖÎ£åÎêòÎ©¥ Îã§Ïãú Ïã§ÌñâÏù¥ ÏïàÎêòÏñ¥ÏÑú
  Î∞òÎ≥µÌï¥ÏÑú Ïã§ÌñâÏãúÌÇ§Î†§ timer Î≥ÄÏàòÎ°ú Í∞êÏã∏Í≥† Î∞òÎ≥µ Ïã§ÌñâÏù∏ setInterval Ï†ÅÏö©
- Ïã§Ìñâ Ïãú ÏÑ§Ï†ïÌï¥Ï§Ä ÏãúÍ∞Ñ(Ï¥à) Îí§Ïóê count Î≥¥Îã§ ÎØ∏ÎßåÏù∏ Í∞ØÏàòÏóê
  makeLight() Ìï®ÏàòÍ∞Ä ÌïúÎ≤à Ïã§ÌñâÎêòÍ≥†
  setIntervalÎ°ú Ï£ºÏñ¥ÏßÑ ÏãúÍ∞Ñ(Ï¥à)ÎèôÏïà Î∞òÎ≥µÌï¥ÏÑú Ïã§Ìñâ
*/
const timer = setInterval(() => {
  for (let i = 0; i < count; i++) {
    setTimeout(() => {
      /*
      makeLight() Ìï®ÏàòÎ•º setintervalÎ°ú Î∞òÎ≥µ Ïã§Ìñâ Ïãú
      Ïã§ÌñâÍ≥º ÎèôÏãúÏóê ÏÉùÏÑ±Ìïú span tagÎ•º Ìï®ÏàòÍ∞Ä Ï¢ÖÎ£åÎê†Îïå htmlÏóêÏÑú ÏÇ≠Ï†úÌï¥Ï§òÏïº
      span tagÍ∞Ä Î∞òÎ≥µÏ†ÅÏúºÎ°ú Í≥ÑÏÜç Î¨¥ÌïúÏúºÎ°ú ÏÉùÏÑ±ÎêòÏßÄ ÏïäÏùå
      */
      const removespan = document.querySelector("span");
